{"command": "node -e "
const { createPublicClient, http, formatUnits } = require('viem');
const { base } = require('viem/chains');

const ERC20_ABI = [
  {
    name: 'balanceOf',
    type: 'function',
    stateMutability: 'view',
    inputs: [{ name: 'account', type: 'address' }],
    outputs: [{ type: 'uint256' }]
  },
  {
    name: 'symbol',
    type: 'function',
    stateMutability: 'view',
    inputs: [],
    outputs: [{ type: 'string' }]
  },
  {
    name: 'decimals',
    type: 'function',
    stateMutability: 'view',
    inputs: [],
    outputs: [{ type: 'uint8' }]
  }
];

const client = createPublicClient({
  chain: base,
  transport: http('https://mainnet.base.org')
});

const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

// Common tokens on Base
const tokens = [
  { address: '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913', name: 'USDC' },
  { address: '0xd9aAEc86B65D86f6A7B5B1b0c42FFA531710b6CA', name: 'DbE' },
  { address: '0x4200000000000000000000000000000000000022', name: 'WETH' },
  { address: '0x6Ca0B9A7F9e89B9d50b7883AE7818F77e5878e62', name: 'DAIMON' }
];

async function main() {
  console.log('Checking token balances for thoth wallet...');
  for (const token of tokens) {
    try {
      const [balance, symbol, decimals] = await Promise.all([
        client.readContract({
          address: token.address,
          abi: ERC20_ABI,
          functionName: 'balanceOf',
          args: [wallet]
        }),
        client.readContract({
          address: token.address,
          abi: ERC20_ABI,
          functionName: 'symbol'
        }),
        client.readContract({
          address: token.address,
          abi: ERC20_ABI,
          functionName: 'decimals'
        })
      ]);
      const formatted = formatUnits(balance, decimals);
      if (Number(formatted) > 0) {
        console.log(symbol + ':', formatted);
      }
    } catch (e) {
      // skip errors
    }
  }
}
main();
""}

