const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');

const client = createPublicClient({
  chain: base,
  transport: http(process.env.BASE_RPC || 'https://mainnet.base.org')
});

async function checkBalance() {
  const address = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';
  const safe = '0x570718D609F6eFd4FBb66bE12Ea65511b8D7e046';

  const walletEth = await client.getBalance({ address });
  const safeEth = await client.getBalance({ address: safe });

  console.log('Wallet ETH:', Number(walletEth) / 1e18);
  console.log('Safe ETH:', Number(safeEth) / 1e18);
  console.log('Total ETH:', (Number(walletEth) + Number(safeEth)) / 1e18);
}

checkBalance().catch(console.error);
