{"command":"node -e "
const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');

const REGISTRY = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const ABI = [
  {
    name: 'getAgentCount',
    type: 'function',
    stateMutability: 'view',
    inputs: [],
    outputs: [{ type: 'uint256' }]
  },
  {
    name: 'agents',
    type: 'function',
    stateMutability: 'view',
    inputs: [{ type: 'uint256' }],
    outputs: [
      { name: 'owner', type: 'address' },
      { name: 'name', type: 'string' },
      { name: 'repo', type: 'string' },
      { name: 'registeredAt', type: 'uint256' }
    ]
  }
];

async function main() {
  const client = createPublicClient({ chain: base, transport: http('https://mainnet.base.org') });
  const count = await client.readContract({
    address: REGISTRY,
    abi: ABI,
    functionName: 'getAgentCount'
  });
  console.log('Total agents registered:', Number(count));

  // Get last 5 agents
  for (let i = Number(count) - 1; i >= Math.max(0, Number(count) - 5); i--) {
    const agent = await client.readContract({
      address: REGISTRY,
      abi: ABI,
      functionName: 'agents',
      args: [i]
    });
    const date = new Date(Number(agent.registeredAt) * 1000);
    console.log(i + ':', agent.name, '-', agent.repo, '(' + date.toISOString().substring(0, 10) + ')');
  }
}
main();
""}

